import './index.scss'
import React, { useEffect,useState } from 'react'
import {
    Container,
    Table,
    TableBody,
    TableContainer,
    TableHead,
    TableRow,
    Avatar
  } from '@mui/material'
import Button from '@mui/material/Button';
import Box from '@mui/material/Box';
import { Grid, Paper } from '@material-ui/core'
import TextField from '@mui/material/TextField';
import Modal from '@mui/material/Modal';
import TableCell, { tableCellClasses } from '@mui/material/TableCell'
import { styled } from '@mui/material/styles'
import Typography from '@mui/material/Typography';
import IconButton from '@mui/material/IconButton';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import Swal from 'sweetalert2'
import { Row, Col, Form} from 'react-bootstrap'
import PartnerModal from '../../component/PartnerModal';

const { Group: FormGroup, Label: FormLabel, Control: FormControl } = Form
const style = {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: 600,
    bgcolor: 'background.paper',
    border: '2px solid #000',
    boxShadow: 24,
    p: 4,
  };
const MangePartnerPage=()=>{
    const [status, setStatus] = useState('')
    const [Operator, setOperator] = useState('')
    const [open, setOpen] = React.useState(false);
    // const handleOpen = () => setOpen(true);
    //const handleClose = () => setOpen(false);
    const [isPartnerModalViseble,setPartnerModalViseble]=useState(false);
    const handleOpen = () => {
        console.log("Hello2")
        setPartnerModalViseble(true)

      }
    const statusOptions = [
        { id: 'active', title: 'Active'},
        { id: 'inactive', title: 'Inactive'},
      ]
    const userData=[
        { id: '1', partnername:'Fourth', status:'active'},
        { id: '2', partnername:'Fourty', status:'inactive'},
        { id: '3', partnername:'Fourthsang', status:'active'},
        { id: '4', partnername:'Fourth Fourth', status:'inactive'}
    ]
    const StyledTableCell = styled(TableCell)(({ theme }) => ({
        [`&.${tableCellClasses.head}`]: {
          backgroundColor: '#4f46e5',
          color: theme.palette.common.white
        },
        [`&.${tableCellClasses.body}`]: {
          fontSize: 14
        }
    }))
    const StyledTableRow = styled(TableRow)(({ theme }) => ({
        '&:nth-of-type(odd)': {
          backgroundColor: theme.palette.action.hover
        },
        // hide last border
        '&:last-child td, &:last-child th': {
          border: 0
        }
    }))
    const confirmDelete =(name)=>{
        Swal.fire({
            title: 'Are you sure?',
            text: `You want to delete ${name}`,
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#3085d6',
            cancelButtonColor: '#d33',
            confirmButtonText: 'Yes, delete it!',
            reverseButtons: true,
          }).then((result) => {
            if (result.isConfirmed) {
              Swal.fire(
                'Deleted!',
                'Your file has been deleted.',
                'success'
              )
            }
          })
    }
    const handleSubmit=()=>{
        Swal.fire(
            'Good job!',
            'Create Partner Success',
            'success'
          )
    }

    console.log("HelloHellllll",isPartnerModalViseble);
    return(
        <>
        <PartnerModal
        // activeContact={activeContact}
        visible={isPartnerModalViseble}
        onClose={() => setPartnerModalViseble(false)}
      />
        <Container className='mangepartner-container'>
            <h1>Mange Partner</h1>
            <Paper className='paper-header'> 
                <Grid container className="searchBar">
                    <Grid item xs={12} sm={12} md={4} lg={3}>
                        <TextField
                            className="filterFormInput"
                            label="Search"
                            placeholder={'Enter Partnername'}
                            variant={'outlined'}
                            fullWidth
                            margin={'normal'}
                            InputLabelProps={{
                                shrink: true
                            }}
                            value={Operator}

                        />
                    </Grid>
                    <Grid item xs={12} sm={12} md={4} lg={3} >
                        <TextField
                            className="filterForm"
                            label="Status"
                            select
                            SelectProps={{
                                native: true
                            }}
                            variant="outlined"
                            margin="normal"
                            fullWidth
                            InputLabelProps={{
                                shrink: true
                            }}
                            size="medium"
                            onChange={(e) => setStatus(e.target.value)}
                            >
                            <option key={''} value={''} hidden>
                                Choose status
                            </option>
                            {statusOptions.map((option, i) => (
                                <option key={`status-option-${i}`} value={option.id} className="option-color">
                                {option.title}
                                </option>
                            ))}
                        </TextField>
                    </Grid>
                    <Grid item xs={2} sm={2} md={1} lg={1}>
                        <Button className="buttonSearchDefault" variant="contained">Search</Button>
                    </Grid>
                    <Grid item sm={'auto'} md={'auto'} lg={3}>
                    </Grid>
                </Grid>
            </Paper>
            <Paper className='paper-table'>
                <Grid className="tableBar">
                    <Grid item sm={'auto'} md={'auto'} lg={2}>
                    </Grid>
                    <Grid>
                        <div>
                            <Button onClick={()=>{handleOpen()}} className="buttonCreatePartnerDefault" variant="contained">Create Partner</Button>

                        </div>
                    </Grid>
                </Grid>
                <TableContainer className='fix-height-monitor'>
                    <Table sx={{ minWidth: 450 }} aria-label="customized table">
                        <TableHead>
                            <TableRow>
                                <StyledTableCell style={{ textAlign: 'center' }}>No.</StyledTableCell>
                                <StyledTableCell style={{ textAlign: 'center' }}>Partner name</StyledTableCell>
                                <StyledTableCell style={{ textAlign: 'center' }}>Status</StyledTableCell>
                                <StyledTableCell style={{ textAlign: 'center' }}>Manage</StyledTableCell>
                            </TableRow>
                        </TableHead>
                        {userData?.length > 0
                        ? <>
                            <TableBody>
                                {userData?.map((row) => (
                                    <StyledTableRow key={row.id}>
                                        <StyledTableCell align="center">{row.id}</StyledTableCell>
                                        <StyledTableCell align="center">{row.partnername}</StyledTableCell>
                                        <StyledTableCell align="center">{row.status}</StyledTableCell>
                                        <StyledTableCell align="center">
                                            <div>
                                          <IconButton>
                                                    <EditIcon/>
                                                </IconButton>
                                                <IconButton onClick={()=>confirmDelete(row.partnername)}>
                                                    <DeleteIcon/>
                                                </IconButton>
                                            </div>
                                        </StyledTableCell>

                                    </StyledTableRow>
                                ))}
                            </TableBody>
                        </>:
                        <>
                            <TableBody>
                                <StyledTableRow>
                                    <StyledTableCell colSpan={6} align="center">ไม่มีข้อมูลเเสดง</StyledTableCell>
                                </StyledTableRow>
                            </TableBody>
                        </>}

                    </Table>
                </TableContainer>
            </Paper>
        </Container>
    </>    
    );
}
export default MangePartnerPage;
