import React, { useEffect,useState } from 'react'
import {
    Avatar
  } from '@mui/material'
import Button from '@mui/material/Button';
import Box from '@mui/material/Box';
import Modal from '@mui/material/Modal';
import Typography from '@mui/material/Typography';
import { Form} from 'react-bootstrap'
import Swal from 'sweetalert2'
import { Row, Col} from 'react-bootstrap'
import TextField from '@mui/material/TextField';
import FormControlLabel from '@mui/material/FormControlLabel'
import Checkbox from '@mui/material/Checkbox'
const { Group: FormGroup, Label: FormLabel, Control: FormControl } = Form
const style = {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: 600,
    bgcolor: 'background.paper',
    border: '2px solid #000',
    boxShadow: 24,
    p: 4,
  };
const PartnerModal=(props)=>{
    const { visible, onClose,type,partner} = props
    const [closeModal,setCloseModal]=useState(visible)
    const [checkedStatusPartner, setCheckedStatusPartner] = useState(partner.status=='active'?true:false);
    const handleChangePartner = (event) => {
        setCheckedStatusPartner(event.target.checked);
    };
    const handleCancel = () => {
        //setCloseModal(false)
        onClose()
    }
    const handleSubmit = async () => {
          Swal.fire(
            'Good job!',
            'Create Partner Success',
            'success'
          )
      }
      useEffect(() => {
        setCloseModal(visible)
      }, [visible])
    console.log("yyyy",closeModal);
    console.log(partner.status)
    const CreatePartner =()=>{
        return(
            <Box sx={style} className='text-box'>
                <Typography id="modal-modal-title" variant="h3" component="h2">
                Create Partner
                </Typography>
                <div className="user-img">
                    <div>
                            <Avatar
                            sx={{ width: 160, height: 160 }}
                            />
                    </div>   
                </div>
                <form onSubmit={handleSubmit}>
                    <FormGroup className="mb-3">
                        <Row>
                            <Col xs={7}>
                                <TextField 
                                label="Image"
                                type="text" 
                                fullWidth
                                />
                            </Col>
                            <Col>
                                <FormControl type="file" />
                            </Col>
                        </Row>
                    </FormGroup>
                        <TextField 
                            label="Partner name"
                            type="text" 
                            fullWidth
                             />
                    <FormGroup>
                        <FormControlLabel 
                            control={<Checkbox
                                checked={true}
                                inputProps={{ 'aria-label': 'controlled' }}
                              />} 
                            label="Active/Inactive" />
                    </FormGroup>
                <FormGroup className="text-center">

                    <Button className='buttonDeleteDefault'  variant="contained" onClick={() => { handleCancel() }}>Cancel</Button>
                    <Button className='buttonSuccessDefault' variant="contained" type="submit">
                        Submit
                    </Button>
                </FormGroup>
                </form>
            </Box>
        )
    }
    function EditPartner(){
        return(
            <Box sx={style} className='text-box'>
                <Typography id="modal-modal-title" variant="h3" component="h2">
                Edit Partner
                </Typography>
                <div className="user-img">
                {partner?.partner_picture === null || partner?.partner_picture === undefined
            ? <>
                <Avatar
                  sx={{ width: 160, height: 160 }}
                  />
              </>
            : <>
                <Avatar
                  src={partner?.partner_picture}
                  sx={{ width: 160, height: 160 }}
                  />
              </>
            }
                </div>
                <form onSubmit={handleSubmit}>
                    <FormGroup className="mb-3">
                        <Row>
                            <Col xs={7}>
                                <TextField 
                                label="Image"
                                type="text" 
                                fullWidth
                                value={partner?.partner_picture}
                                />
                            </Col>
                            <Col>
                                <FormControl type="file" />
                            </Col>
                        </Row>
                    </FormGroup>
                        <TextField 
                            label="Partner name"
                            type="text" 
                            fullWidth
                            value={partner.partnername} />
                    <FormGroup>
                        <FormControlLabel 
                            control={<Checkbox
                                checked={checkedStatusPartner}
                                onClick={handleChangePartner}
                                inputProps={{ 'aria-label': 'controlled' }}
                              />} 
                            label="Active/Inactive" />
                    </FormGroup>
                <FormGroup className="text-center">

                    <Button className='buttonDeleteDefault'  variant="contained" onClick={() => { handleCancel() }}>Cancel</Button>
                    <Button className='buttonSuccessDefault' variant="contained" type="submit">
                        Submit
                    </Button>
                </FormGroup>
                </form>
            </Box>
        )
    }
    return(
        <Modal
            title="Forward operator"
            //visible={visible}
            //onCancel={() => { handleCancel() }}
            //onOk={() => handleSubmit()}
            open={closeModal}
            onClose={() => { handleCancel() }}
            aria-labelledby="modal-modal-title"
            aria-describedby="modal-modal-description"
        >
            {type=='create'?
                CreatePartner()
                :
                EditPartner()
            }

        </Modal>
    )
}
export default PartnerModal
